html:
{% set arabic_days = {
    'Saturday': 'السبت',
    'Sunday': 'الأحد',
    'Monday': 'الإثنين',
    'Tuesday': 'الثلاثاء',
    'Wednesday': 'الأربعاء',
    'Thursday': 'الخميس',
    'Friday': 'الجمعة'
} %}


<!DOCTYPE html>
<html>
    <head>
        <style>
            .row {
               display: flex;
            } 

            .row:before, .row:after{
                content: none;
            }

            .g-3{
                gap: 20px;
            }
            .col-8{
                flex: 3;
                text-align: center;
            }
            .col-6 , .col-1 , .col-3{
                flex: 2;
                text-align: center;    
            }
            .col-4 {
                flex: 1;
                text-align: center;  
            }
            .items, .summary_table {
                width: 100%; 
                border-collapse: collapse;
                margin-top: 10px;
                margin-bottom: 10px;
            }

            .items th, .items td , .summary_table td {
                border: 1px solid #2F2F2F;
                padding: 2px !important;
                text-align: center;
            }
            .items th {
                background-color: #555;
            }
            .info td{
                padding: 4px !important;
            }
            .print-format {
                padding: 0%;
            }
        </style>
    </head>
    <body>
        <div class="container"  width="100%">
            <div class="header" width="100%">
                <img src="/assets/dynamic/images/healthy_corner_header_without_tax_info.png" width="100%">
                <hr style="border: 3px solid #000">
            </div>

            <div class = "row">
                <div class = "col-11">
                    <table class="info" style=" font-size:10px;">
                        <tr>
                            <td width="20%" style="font-weight: bold; text-align:left;">اليوم |</td>
                            {% set current_day = frappe.utils.format_date(frappe.utils.today(), 'EEEE') %}
							<td width="" style="text-align:right;">{{ arabic_days[current_day] or current_day }}</td>
                            <td width="" style="font-weight: bold; text-align:left;">التاريخ |</td>
                            <td width="12%" style="text-align:right;">{{ doc.posting_date }}</td>
                            <td width="15%" style="font-weight: bold; text-align:left;"></td>
                            <td width="" style="text-align:right;"></td>
                        </tr>
                        <tr>
                            {% if doc.payment_type == 'Pay' or doc.payment_type == 'دفع' %}
                                <td width="" style="font-weight: bold; text-align:left;">رقم إذن الدفع |</td>
                            {% else %}
                                <td width="" style="font-weight: bold; text-align:left;">رقم أذن التحصيل |</td>
                            {% endif %}
                            <td width="" style="text-align:right;">{{ doc.name }}</td>
                            <td width="" style="font-weight: bold; text-align:left;"></td>
                            <td width="" style="text-align:right;"></td>
                            <td width="" style="font-weight: bold; text-align:left;"></td>
                            <td width="" style="text-align:right;"></td>
                        </tr>
                        <tr>
                            {% if doc.payment_type == 'Pay' or doc.payment_type == 'دفع' %}
                                <td width="" style="font-weight: bold; text-align:left;">جهة الصرف |</td>
                                <td width="" style="text-align:right;">{{ doc.paid_from }}</td>
                                <td width="" style="font-weight: bold; text-align:left;">العملة |</td>
                                <td width="" style="text-align:right;">{{ doc.paid_from_account_currency }}</td>
                                <td width="" style="font-weight: bold; text-align:left;">سعر الصرف |</td>
                                <td width="" style="text-align:right;">{{ doc.source_exchange_rate}}</td>
                            {% else %} 
                                <td width="" style="font-weight: bold; text-align:left;">جهة التحصيل |</td>
                                <td width="" style="text-align:right;">{{ doc.paid_to }}</td>
                                <td width="" style="font-weight: bold; text-align:left;">العملة |</td>
                                <td width="" style="text-align:right;">{{ doc.paid_to_account_currency }}</td>
                                <td width="" style="font-weight: bold; text-align:left;">سعر الصرف |</td>
                                <td width="" style="text-align:right;">{{ doc.target_exchange_rate}}</td>
                            {% endif %}
                        </tr>
                        <tr>
                            <td width="15%" style="font-weight: bold; text-align:left;">رقم القيد |</td>
                            <td width="" style="text-align:right;"></td>
                            <td width="13%" style="font-weight: bold; text-align:left;"></td>
                            <td width="" style="text-align:right;"></td>
                            <td width="" style="font-weight: bold; text-align:left;"></td>
                            <td width="" style="text-align:right;"></td>
                        </tr>
                        <tr>
                            {% if doc.payment_type == 'Pay' or doc.payment_type == 'دفع' %}
                                <td width="15%" style="font-weight: bold; text-align:left;">طريقة الدفع |</td>
                                <td width="" style="text-align:right;">{{ doc.mode_of_payment }}</td>

                                <td width="13%" style="font-weight: bold; text-align:left;"></td>
                                <td width="" style="text-align:right;"></td>
                                <td width="" style="font-weight: bold; text-align:left;">الحساب |</td>
                                <td width="" style="text-align:right;">{{doc.paid_to}}</td>
                            {% else %} 
                            <td width="15%" style="font-weight: bold; text-align:left;">طريقة التحصيل |</td>
                                <td width="" style="text-align:right;">{{ doc.mode_of_payment }}</td>
                                <td width="13%" style="font-weight: bold; text-align:left;"></td>
                                <td width="" style="text-align:right;"></td>
                                <td width="" style="font-weight: bold; text-align:left;">الحساب |</td>
                                <td width="" style="text-align:right;">{{doc.paid_from}}</td>
                            {% endif %}

                        </tr>
                    </table>
                </div>
                <div class = "col-1">
                    <table>
                        <tr>
                            <td style="text-align: center;">  قرش  | جنيه</td>
                        </tr>
                        <tr>
                            <td style="text-align: center;">{{ ("%.2f" | format(doc.paid_amount)) or 0.00 }}</td>
                        </tr>
                    </table>

                </div> 
            </div>

            <table class="items" style=" font-size:10px;  margin-top: 30px;  margin-bottom: 30px;">
                <tr>
                    <th width="" style="font-weight: bold;">النوع</th>
                    {% if doc.payment_type == 'Pay' or doc.payment_type == 'دفع' %}
                        <th width="" style="font-weight: bold;">يصرف إلى</th>
                    {% else %}
                        <th width="" style="font-weight: bold;">وارد من</th>
                    {% endif %}


                    <th width="" style="font-weight: bold;">المبلغ</th>
                    {% if doc.mode_of_payment == "Cheque" or doc.mode_of_payment == "بنك" %}
                        <th width="" style="font-weight: bold;">البنك</th>
                        <th width="" style="font-weight: bold;">تاريخ الإستحقاق</th>
                    {% endif %}
                    <th width="10%" style="font-weight: bold;">المستلم</th>
                    <th width="" style="font-weight: bold;">البيان</th>
                </tr>
                    <tr>
                        <td>{{ doc.party_type or ""}}</td>
                        <td>{{ doc.party_name or ""}}</td>
                        <td style="text-align: right;">{{ ("%.2f" | format(doc.paid_amount)) or 0.00 }}</td>
                        {% if doc.mode_of_payment == "Cheque" or doc.mode_of_payment == "بنك" %}
                            <td>{{ doc.reference_no }}</td>
                            <td>{{ doc.reference_date }}</td>
                        {% endif %}
                        <td></td>
                        <td>{{ doc.remarks or ""}}</td>
                    </tr>
            </table>
            <div class="row" style="margin-top: 10px;">
                <div> المبلغ | {{ doc.in_words or doc.paid_amount }} </div>
            </div>
            <div class="row" style="margin-top: 30px;">
                <div class="col-3" style="text-align: center;">الخزينة</div>
                {% if doc.payment_type == 'Pay' or doc.payment_type == 'دفع' %}
                    <div class="col-3" style="text-align: center;">المستلم</div>
                {% else %}
                    <div class="col-3" style="text-align: center;">المسلم</div>
                {% endif %}
                <div class="col-3" style="text-align: center; ">المدير المباشر</div>
                <div class="col-3" style="text-align: center;">الإعتماد</div>
            </div>

        </div>
    </body> 
</html>
